import React from 'react';
import {cards} from '../../../../data/cards.js'

const ships = ({faction}) => {
    return (
        <div>
            {cards.ship.map((s) => {
                if (s.faction === faction) {
                    return (
                        <div className="card-display span-1-of-3">
                            <img key={s.id} src={"/images/cards/ship/" + faction + "/" + s.image} alt="img"/>
                        </div>

                    )
                }
            })}
        </div>
    )
};

export default ships;
