import React from 'react';
import {cards} from '../../../../data/cards.js'


const display = ({faction}) => {
    return (
        <div className="ship-card span-1-of-3">
            {cards.ship.map((s) => {

                if (s.faction === faction){
                    return (
                        <div>
                            <img src={"/images/cards/ship/" + faction + "/" + s.image} alt="img"/>
                        </div>
                    )
                    } else {
                    return(
                        s.faction
                )
                }

            }
            )}
            {/*<img src={require("/Users/chriscaldwell/IdeaProjects/armada-using-react/src/images/home-page/rebel-logo.png")}></img>*/}
        </div>
    )
};

export default display;
