import React from 'react';
import {cards} from '../../../data/cards.js'


class ships extends React.Component {
    //accessing the title of the clicked on ship
    addShip = () =>{
        const doesShow = this.state.isShowing;
        this.setState({isShowing: !doesShow})
    };


    render(){
        return (
            <div className="cards-container">
                {cards.ship.map(s => {
                    if (s.faction === "imperial") {
                        return (
                            <div className="ship-card span-1-of-3" onClick={this.addShip}>
                                <img key={s.id} src={"/images/cards/ship/imperial/" + s.image} alt="img"/>
                            </div>
                        )
                    }
                })}
            </div>
        )
    }
}

export default ships;
